$(document).ready((function(){$("[data-bs-hover-animate]").mouseenter((function(){var e=$(this);e.addClass("animated "+e.attr("data-bs-hover-animate"))})).mouseleave((function(){var e=$(this);e.removeClass("animated "+e.attr("data-bs-hover-animate"))}))}));const bulb=document.getElementById("bulb");bulb.onclick=()=>{document.documentElement.classList.toggle("dark-mode"),document.querySelectorAll(".inverted").forEach(e=>{e.classList.toggle("invert")})};